import React from 'react';
import { Formik } from 'formik';
import * as Yup from 'yup';
import './app.css';
function App() {
  return (
    <div className='container'>
      <div className='brand-box'>
        <h1>Magic Form</h1>
        <p>Build form in react</p>
      </div>
      <div className='magic-form'>
        <Formik
          initialValues={{
            name: '',
            email: '',
            agree: false,
            favoriteColor: '',
          }}
          validationSchema={Yup.object({
            name: Yup.string().required('Ad bosh ola bilmez'),
            email: Yup.string()
              .email('duzgun email adresi deyil')
              .required('email bosh ola bilmez'),
agree:Yup.boolean().required('shertleri qebul etmelisen'),
favoriteColor

          })}
        />
        <Formik />
      </div>
    </div>
  );
}

export default App;
