import React from 'react';
import { Formik } from 'formik';
import * as Yup from 'yup';
import './app.css';

const App = () => {
  return (
    <div className='container'>
      <div className='brand-box'>
        <h1>Qeydiyyat Formu</h1>
        <p>Reacta derslerinde her kese ugurlar</p>
      </div>
      <div className='magic-form'>
        <Formik
          initialValues={{
            name: '',
            email: '',
            agree: false,
            favoriteColor: '',
          }}
          validationSchema={Yup.object({
            name: Yup.string().required('Ad bosh ola bilmez'),
            email: Yup.string()
              .email()
              .required('email bosh ola bilmez'),
            agree: Yup.boolean().required('shertleri qebul etmelisen'),
            favoriteColor: Yup.string()
              .required('yaxshi gorek, her kesin sevdiyi bir reng var')
              .oneOf(['red', 'blue', 'green'], 'duzgun renk secin'),
          })}
          onSubmit={(values, { resetForm, setSubmitting }) => {
            console.log(values);
            setTimeout(() => {
              setSubmitting(false);
              resetForm();
            }, 2000);
          }}
        >
          {({
            values,
            errors,
            handleChange,
            handleSubmit,
            handleReset,
            dirty,
            touched,
            isSubmitting,
          }) => (
            <form onSubmit={handleSubmit}>
              <h3>qeydiyyatdan kec</h3>
              <label htmlFor='name'>ad</label>
              <input
                id='name'
                type='text'
                placeholder='Anar....'
                className='input'
                value={values.name}
                onChange={handleChange}
              />
              {errors.name && touched.name && (
                <div className='error'>{errors.name}</div>
              )}
              <label htmlFor='email'>email</label>
              <input
                id='email'
                type='email'
                placeholder='anarnasibov000@gmail.com'
                className='input'
                value={values.email}
                onChange={handleChange}
              />
              {errors.email && touched.email && (
                <div className='error'>{errors.email}</div>
              )}
              <label
                htmlFor='favoriteColor'
                className='topMargin'
              >
                sevdiyin reng
              </label>
              <select
                id='favoriteColor'
                value={values.favoriteColor}
                onChange={handleChange}
                style={{
                  marginTop: 10,
                  width: '150px',
                  padding: '10px 15px',
                  outline: 'none',
                }}
              >
                <option
                  value=''
                  label='reng sec'
                />
                <option
                  value='red'
                  label='qirmizi'
                />
                <option
                  value='blue'
                  label='mavi'
                />
                <option
                  value='green'
                  label='yashil'
                />
              </select>
              {errors.favoriteColor && touched.favoriteColor && (
                <div className='error'>{errors.favoriteColor}</div>
              )}
              <div className='checkbox topMargin'>
                <input
                  id='agree'
                  type='checkbox'
                  checked={values.agree}
                  onChange={handleChange}
                />

                <label
                  htmlFor='agree'
                  className='checkbox-label'
                >
                  shertleri oxudum qebul edirem
                </label>
              </div>
              <button
                type='submit'
                disabled={!dirty || isSubmitting}
              >
                qeydiyyat
              </button>
            </form>
          )}
        </Formik>
      </div>
    </div>
  );
};

export default App;
