import React from 'react';
import { Formik } from 'formik';
import * as Yup from 'yup';
import './app.css';

function App() {
  return (
    <div className='container'>
      <div className='brand-box'>
        <h1>Magic Form</h1>
        <p>Build form in react</p>
      </div>
      <div className='magic-form'>
        <Formik
          initialValues={{
            name: '',
            email: '',
            agree: false,
            favoriteColor: '',
          }}
          validationSchema={Yup.object({
            name: Yup.string().required('Ad bosh ola bilmez'),
            email: Yup.string()
              .email('duzgun email adresi deyil')
              .required('email bosh ola bilmez'),
            agree: Yup.boolean().required('shertleri qebul etmelisen'),
            favoriteColor: Yup.string()
              .required('yaxshi gorek, her kesin sevdiyi bir reng var')
              .oneOf(['red', 'blue', 'green'], 'duzgun renk secin'),
          })}
        >
          {({
            values,
            errors,
            handleChange,
            handleSubmit,
            handleReset,
            dirty,
            isSubmitting,
          }) => (
            <form>
              <h3>qeydiyyatdan kec</h3>
              <label htmlFor='name'>ad</label>
              <input
                id='name'
                type='text'
                placeholder='Anar....'
                className='input'
                value={values.name}
                onChange={handleChange}
              />

              <label htmlFor='email'>email</label>
              <input
                id='email'
                type='email'
                placeholder='anarnasibov000@gmail.com'
                className='input'
                value={values.email}
                onChange={handleChange}
              />
              <label
                htmlFor='favoriteColor'
                className='topMargin'
              >
                sevdiyin reng
              </label>
              <select
              id='favoriteColor' value={values.favoriteColor}

              >

              </select>
            </form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default App;
